from collections import Counter


def elevator(buttons_pressed):
    c = Counter(buttons_pressed)
    return c.get('(', 0) - c.get(')', 0)


def basement_finder(buttons_pressed):
    position = 0
    for index, button in enumerate(buttons_pressed, start=1):
        if button == '(':
            position += 1
        elif button == ')':
            position -= 1

        if position == -1:
            return index


